sqlMap.select:
select count(*) from ( select
from ( select t0.CUID as t0_CUID, t0.LABEL_CN as t0_LABEL_CN, t0.ORDER_PLAN_ID as t0_ORDER_PLAN_ID, t0.TASKBOM_ID as t0_TASKBOM_ID, t0.ORDER_TASK_ID as t0_ORDER_TASK_ID, t0.DEPARTMENT_ID as t0_DEPARTMENT_ID, t0.USER_ID as t0_USER_ID, t0.PLAN_DATE as t0_PLAN_DATE, t0.PLAN_START_TIME as t0_PLAN_START_TIME, t0.PLAN_END_TIME as t0_PLAN_END_TIME, t0.STATE as t0_STATE, t0.COMPLETE_NUM as t0_COMPLETE_NUM, t0.CAR_NUM as t0_CAR_NUM, t0.PLAN_NUM as t0_PLAN_NUM, t0.BOM_ID as t0_BOM_ID, zbz as t0_zbz, nvl(audit_num,0) as t0_audit_num , nvl(move_num,0) as t0_move_num, nvl(back_num,0) as t0_back_num, nvl(out_num,0) as t0_out_num, nvl(move_num_in,0) as t0_move_num_in, nvl(wait_back_num,0) as t0_wait_back_num, nvl(storage_num,0) as t0_storage_num from ZG_T_ORDER_PLANBOM t0 ) m left join (select t0_LABEL_CN, t0_cuid FROM (select taskbom.cuid as t0_cuid ,t0.LABEL_CN as t0_LABEL_CN from ZG_T_ORDERBOM t0, zg_t_order_taskbom taskbom where t0.cuid = taskbom.order_bom_id) t0) t0_0_1 on t0_0_1.t0_CUID = m.t0_taskBOM_ID left join (select t0.CUID as t0_CUID, t0.AUFNR as t0_AUFNR from ZG_T_ORDER_TASK t0) t0_1_1 on t0_1_1.t0_CUID = m.t0_ORDER_TASK_ID left join ( select t0.CUID as t0_CUID, t0.LABEL_CN as t0_LABEL_CN, t0.PARENT_ORG_ID as t0_PARENT_ORG_ID, t0.LEVEL_NUM as t0_LEVEL_NUM, t0.SEQ as t0_SEQ, t0.NOTE as t0_NOTE, t0.RELATED_BM_CLASS_ID as t0_RELATED_BM_CLASS_ID from FW_ORGANIZATION t0 ) t0_2_1 on t0_2_1.t0_CUID = m.t0_DEPARTMENT_ID left join ( select t1.CUID as t1_CUID, t1.LABEL_CN as t1_LABEL_CN, t1.USER_ID as t1_USER_ID, t1.PASSWORD as t1_PASSWORD, t1.CREATE_TIME as t1_CREATE_TIME, t1.LAST_MODIFY_TIME as t1_LAST_MODIFY_TIME, t1.EFFECT_TIME as t1_EFFECT_TIME, t1.STATUS as t1_STATUS, t1.RELATED_BM_CLASS_ID as t1_RELATED_BM_CLASS_ID, t0.CUID as t0_CUID, t0.SEX as t0_SEX, t0.MOBILE as t0_MOBILE, t0.TELEPHONE as t0_TELEPHONE, t0.ADDRESS as t0_ADDRESS, t0.EMAIL as t0_EMAIL from FW_OPERATOR t1, FW_EMPLOYEE t0 where t1.CUID = t0.CUID ) t0_3_1 on t0_3_1.t0_CUID = m.t0_USER_ID
)
