select count(1) from ( select from (select fe.cuid fe_employeeId, fo.label_cn fo_employeeName, fo.rfidcode, fe.sex, fe.mobile, fe.telephone, fe.address, fe.email, fon.label_cn org_id_label_cn, fon.cuid departmentId from FW_OPERATOR fo, FW_EMPLOYEE fe,FW_ORGANIZATION fon where fo.cuid = fe.cuid and fe.org_id=fon.cuid(+)
and fo.cuid not in ($employeesId$)
)m
)
